package parctice;

public class 极限 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("byte的最大上限为"+Byte.MAX_VALUE+"最小下限制为"+Byte.MIN_VALUE);
		System.out.println("short的最大上限为"+Short.MAX_VALUE+"最小下限制为"+Short.MIN_VALUE);
		System.out.println("int的最大上限为"+Integer.MAX_VALUE+"最小下限为"+Integer.MIN_VALUE);
		System.out.println("long的最大上限为"+Long.MAX_VALUE+"最小下限制为"+Long.MIN_VALUE);
		System.out.println("float的最大上限为"+Float.MAX_VALUE+"最小下限制为"+Float.MIN_VALUE);
		System.out.println("double的最大上限为"+Double.MAX_VALUE+"最小下限制为"+Double.MIN_VALUE);
	}

}
